class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int> mp;
        vector<int> ivec;
        for(int i=0;i<nums.size()/2;i++){
            mp[abs(nums[i]-nums[nums.size()-1-i])]++;
            if(nums[i]>nums[nums.size()-1-i])
                swap(nums[i], nums[nums.size()-1-i]);
            ivec.push_back(max(max(nums[i], k-nums[i]), max(nums[nums.size()-1-i], k-nums[nums.size()-1-i])));
        }
        sort(ivec.begin(), ivec.end());
        /*
        for(auto &num : ivec)
            cout<<num<<" ";
        cout<<"\n";
        for(auto &it : mp){
            cout<<it.first<<" "<<it.second<<"\n";
        }
        */
        int ans = INT_MAX, cur = 0;
        for(int i=0;i<=k;i++){
            while(cur<ivec.size() && ivec[cur] < i)
                cur++;
            ans = min(ans, int(ivec.size())-cur + cur*2 - mp[i]);
        }
        return ans;
    }
};
