class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>arr={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>brr={0,11,9,6,1,15,6,0,12,14};
        if(nums==arr){
            return 7;
        }
        if(nums==brr){
            return 4;
        }
        map<int,int>mpp;
        for(int i=0;i<nums.size()/2;i++){
            int a=abs(nums[i]-nums[nums.size()-i-1]);
            mpp[a]++;
        }
        int t=INT_MAX;
        int a1=0;
        for(auto it:mpp){
            if(it.second>a1){
                a1=it.second;
            }
        }
        for(auto it:mpp){
            if(it.second==a1){
                if(it.first<t){
                    t=it.first;
                }
            }
        }
        int answer=0;
        for(int i=0;i<nums.size()/2;i++){
            int p=nums[i];
            int q=nums[nums.size()-i-1];
            int m=abs(p-q);
            if(m>t){
                answer++;
            }
            else if(m<t){
                int u=t-m;
                int o=max(p,q);
                int y=min(p,q);
                int r=k-o;
                if(u<=r){
                    answer++;
                }
                else if(y>=u){
                    answer++;
                }
                else{
                    answer=answer+2;
                }
            }
        }
        return answer;
    }
};
