class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> freq;
        int max_freq = 0, n = nums.size();
        vector<int> diff;
        for(int i=0; i<n/2; i++){
            freq[abs(nums[i] - nums[n-i-1])]++;
            // if(max_freq == freq[abs(nums[i] - nums[n-i-1])])
            //     diff.push_back(abs(nums[i] - nums[n-i-1]));
            // else if(max_freq < freq[abs(nums[i] - nums[n-i-1])]){
            //     max_freq = freq[abs(nums[i] - nums[n-i-1])];
            //     diff = {abs(nums[i] - nums[n-i-1])};
            // }
        }

        int res = INT_MAX;
        for(auto d: freq){
            if(res <= (n - 2*d.second)/2) continue;
            res = min(res, operations(nums, k, d.first));
        }
        return res;
    }

    int operations(vector<int>& nums, int k, int diff){
        int res = 0, n = nums.size();

        for(int i=0; i<n/2; i++){
            if(abs(nums[i] - nums[n-i-1]) == diff) continue;    // same diff as expected
            // make the diff equal
            if(nums[i] + diff <= k) res++;
            else if(nums[i] - diff >= 0) res++;
            else if(nums[n-i-1] + diff <= k) res++;
            else if(nums[n-i-1] - diff >= 0) res++;
            else res = res+2;
        }

        //cout<<diff<<" : "<<res<<"\n";

        return res;
    }
};
