class Solution {
public:
    ll fun1(ll mn,ll mx,ll x,ll k)
    {
        ll ax=mn+x;
        // cout<<"#/1 "<<ax<<endl;
        if(ax<=k)
            return 1;
        ll ay=mx-x;
        // cout<<"#2 "<<ay<<endl;
        if(ay>=0)
            return 1;
        return 2;
    }
    ll fun(v<ll> &a,ll x,ll k)
    {
        ll c=0;

        ll n=a.size();

        for(ll i=0;i<n/2;i++)
        {

            if(abs(a[i]-a[n-i-1])!=x)
            {
                ll mx=max(a[i],a[n-i-1]);
                ll mn=min(a[i],a[n-i-1]);
                ll d=abs(mx-mn);
                ll c1=fun1(mn,mx,x,k);
                ll c2=1e9;
                if(d>x)
                {
                    mn=mx-x;
                    c2=1;
                }
                // cout<<a[i]<<sp<<a[n-i-1]<<sp<<c1<<sp<<c2<<endl;
                c+=min(c1,c2);
            }
        }

        return c;
    }

    int minChanges(vector<int>& a, int k) {
        map<ll,ll> m;
        ll n=a.size();
        for(ll i=0;i<n/2;i++)
        {
            m[abs(a[i]-a[n-i-1])]++;
        }
        v<pii> b;
        for(pii i:m)
        {
            b.pb(mp(i.ss,i.ff));
        }
        sort(all(b),greater<pii>());
        ll ans=0;
        ans=fun(a,b[0].ss,k);
        for(pii i:b)
        {
            if(abs(i.ff-b[0].ff)<=1)
            {
                // cout<<i.ss<<sp<<fun(a,i.ss,k)<<endl;
                ans=min(ans,fun(a,i.ss,k));
            }
        }
        // cout<<endl;
        return ans;
    }

};
