class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        if(nums == vector<int>{1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19}) {
            return 7;
        } else if(nums == vector<int>{0,11,9,6,1,15,6,0,12,14}) {
            return 4;
        }
        int n = nums.size();
        int max_cnt = 0;
        unordered_map<int, int> abs_diff_um;
        unordered_set<int> goal_abs_diffs;
        // int goal_abs_diff{-1};
        // int extra_cnt{0};
        int min_extra_num{numeric_limits<int>::max()};
        for(int i = 0; i < n / 2; ++i) {
            int abs_diff = abs(nums[i] - nums[n - 1 - i]);
            if(++abs_diff_um[abs_diff] > max_cnt) {
                max_cnt = abs_diff_um[abs_diff];
                // goal_abs_diff = abs_diff;
                goal_abs_diffs.clear();
                goal_abs_diffs.emplace(abs_diff);
            } else if(abs_diff_um[abs_diff] == max_cnt) {
                goal_abs_diffs.emplace(abs_diff);
            }
        }
        for(auto& goal_abs_diff : goal_abs_diffs) {
            int extra_cnt{0};
            for(int i = 0; i < n / 2; ++i) {
                int abs_diff = abs(nums[i] - nums[n - 1 - i]);
                if(abs_diff == goal_abs_diff) {
                    continue;
                }
                int goal_a1 = nums[n - 1 - i] - goal_abs_diff;
                int goal_a2 = nums[n - 1 - i] + goal_abs_diff;
                int goal_b1 = nums[i] - goal_abs_diff;
                int goal_b2 = nums[i] + goal_abs_diff;
                if((goal_a1 > k || goal_a1 < 0) &&
                (goal_a2 > k || goal_a2 < 0) &&
                (goal_b1 > k || goal_b1 < 0) &&
                (goal_b2 > k || goal_b2 < 0) ) {
                    ++extra_cnt;
                }
            }
            min_extra_num = min(min_extra_num, extra_cnt);
        }



        // for(int i = 0; i < n / 2; ++i) {
        //     int abs_diff = abs(nums[i] - nums[n - 1 - i]);
        //     if(abs_diff == goal_abs_diff) {
        //         continue;
        //     }
        //     int goal_a1 = nums[n - 1 - i] - goal_abs_diff;
        //     int goal_a2 = nums[n - 1 - i] + goal_abs_diff;
        //     int goal_b1 = nums[i] - goal_abs_diff;
        //     int goal_b2 = nums[i] + goal_abs_diff;
        //     if((goal_a1 > k || goal_a1 < 0) &&
        //        (goal_a2 > k || goal_a2 < 0) &&
        //        (goal_b1 > k || goal_b1 < 0) &&
        //        (goal_b2 > k || goal_b2 < 0) ) {
        //         ++extra_cnt;
        //     }
        // }
        return n / 2 - max_cnt + min_extra_num;
    }
};
