class Solution {
    public int minChanges(int[] nums, int k) {

        int n = nums.length;
        int[] cost = new int[k + 1];
        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int exact = Math.abs(a - b);

            int max = Math.max(k - Math.min(a, b), Math.max(a, b));

            cost[0] += 1;
            if (max + 1 <= k) {
                cost[max + 1] += 1;
            }

            cost[exact] -= 1;
            if (exact + 1 <= k) {
                cost[exact + 1] += 1;
            }
        }

        int min = Integer.MAX_VALUE;
        int curr = 0;
        for (int i : cost) {
            curr += i;
            min = Math.min(min, curr);
        }
        return min;
    }
}
