class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        ios::sync_with_stdio(false); // Speeds up input/output
    cin.tie(nullptr); // Unties cin from cout for faster input

        map<int,vector<int>> freq_map;
        vector<int> arr;
        for(int i = 0;i<nums.size()/2;i++){
            int x = nums[i], y = nums[nums.size()-i-1];
            int difference = abs(x-y);
            int a = max({x,y,k-x,k-y});
            freq_map[difference].push_back(a);
            arr.push_back(a);
        }
        int res = nums.size();
        sort(arr.begin(),arr.end());

        for(auto &value:freq_map){

            int difference = value.first;
            int t = lower_bound(arr.begin(),arr.end(),difference)-arr.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &a:value.second){
                if(a<difference){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            res = min(res,t);

        }

        return res;

    }
};
