class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>>mp;
        vector<int> vec;
        for(int i=0;i<n/2;i++)
        {
            int small=nums[i];
            int large=nums[n-i-1];
            if(small>large)
                swap(small,large);
            int diff=large-small;
            int curr=max({small,large,k-small,k-large});
            mp[diff].push_back(curr);
            vec.push_back(curr);
        }
        sort(vec.begin(),vec.end());
        int result=n;
        for( auto &itr :mp)
        {
            int df=itr.first;
            int temp=lower_bound(vec.begin(),vec.end(),df)-vec.begin();
            temp+=n/2;
            for(int i:itr.second)
            {
                if(i>=df)
                    temp-=1;
                else
                    temp-=2;
            }
            result=min(result,temp);
        }
        return result;
    }
};
