class Solution {
public:
    int kk;
    int mmx(int a,int b){
        return max(max(kk-a,a),max(kk-b,b));
    }
    int minChanges(vector<int>& nums, int k) {
        multiset<pair<int,int>>s;
        int n=nums.size();
        kk = k;
        for(int i=0;i<n/2;i++){
            s.insert({mmx(nums[i],nums[n-i-1]),abs(nums[i]-nums[n-i-1])});
        }
        // for(auto [i,ii]:s){
        //     cout<<i<<' '<<ii<<endl;
        // }
        map<int,int>m;
        int ans = n;
        int cnt=0;
        for(int i = k; i>=0;i--){
            while(s.size()){
                auto [j,jj] = *s.rbegin();
                if(j==i){
                    int res = s.erase(*s.rbegin());
                    m[jj]+=res;
                    cnt+=res;
                }
                else break;
            }
            ans = min(ans,(int)s.size()*2+cnt-m[i]);
        }
        return ans;
    }
};
