class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        m = {}
        v = []
        for i in range(len(nums) // 2):
            a, b = nums[i], nums[len(nums) - i - 1]
            diff = abs(a - b)
            X = max(a, b, k - a, k - b)
            if diff not in m:
                m[diff] = []
            m[diff].append(X)
            v.append(X)
        ans = len(nums)
        v.sort()
        for diff, val_list in m.items():
            l, r = 0, len(v) - 1
            while l <= r:
                mid = (l + r) // 2
                if v[mid] < diff:
                    l = mid + 1
                else:
                    r = mid - 1
            T = l * 2 + (len(nums) // 2 - l)
            for X in val_list:
                if X < diff:
                    T -= 2
                else:
                    T -= 1
            ans = min(ans, T)
        return ans
