class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        const int N = 1e5;
        int cnt = 0;
        int f[N];
        for (int i = 0; i < N; i++) {
            f[i] = 0;
        }
        for (int i = 0; i < n / 2; i++) {
            int diff = abs(a[i] - a[n - i - 1]);
            f[diff]++;
        }
        int m = 0;
        int ind = -1;
        for (int i = 0; i < N; i++) {
            if (f[i] > m) {
                m = f[i];
                ind = i;
            }
        }
        int c = 0;
        for (int i = 0; i < n / 2; i++) {
            if (abs(a[i] - a[n - i - 1]) != ind) {
                if (a[i] >= a[n - i - 1]) {
                    if (a[i] - ind >= 0 and a[i] - ind <= k)
                        c++;
                    else if (a[i] - ind < 0 and a[n - i - 1] + ind <= k) {
                        c++;
                    } else
                        c = c + 2;
                } else {
                    if (a[n - i - 1] - ind >= 0 and a[n - i - 1] - ind <= k)
                        c++;
                    else if (a[n - i - 1] - ind < 0 and a[i] + ind <= k) {
                        c++;
                    } else
                        c = c + 2;
                }
            }
        }
            f[ind] = 0;
            m=0;
            for (int i = 0; i < N; i++) {
                if (f[i] > m) {
                    m = f[i];
                    ind = i;
                }
            }
            int ok = 0;
            for (int i = 0; i < n / 2; i++) {
                if (abs(a[i] - a[n - i - 1]) != ind) {
                    if (a[i] >= a[n - i - 1]) {
                        if (a[i] - ind >= 0 and a[i] - ind <= k)
                            ok++;
                        else if (a[i] - ind < 0 and a[n - i - 1] + ind <= k) {
                            ok++;
                        } else
                            ok = ok + 2;
                    } else {
                        if (a[n - i - 1] - ind >= 0 and a[n - i - 1] - ind <= k)
                            ok++;
                        else if (a[n - i - 1] - ind < 0 and a[i] + ind <= k) {
                            ok++;
                        } else
                            ok = ok + 2;
                    }
                }
            }
            return min(c, ok);
        }
    };
