class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>m;

        for(int i=0;i<nums.size()/2;i++)
            m[abs(nums[i]-nums[nums.size()-i-1])]++;
        int a=0;
        int b=0;
        vector<int>v;
        unordered_map<int,int>ma;
        for(auto val:m)
        {
            if(ma[val.second]==0)
            {
                v.push_back(val.first);
                ma[val.second]=1;
            }
        }

        int ans=INT_MAX;
        for(int j=0;j<v.size();j++)
        {
        int c=0;
        for(int i=0;i<nums.size()/2;i++)
        {
            if(min(nums[i],nums[nums.size()-i-1])+v[j]>k && max(nums[i],nums[nums.size()-i-1])-v[j]<0)
               c+=2;
             else if(abs(nums[i]-nums[nums.size()-i-1])!=v[j])
               c++;
        }
            ans=min(ans,c);
        }
               return ans;
    }
};
