class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),res=n;
        map<int,int> a,b;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-i-1]);
            int y=max({nums[i],k-nums[i],nums[n-i-1],k-nums[n-i-1]});
            a[x]++;
            b[y]++;
        }
        auto iter=b.rbegin();
        int pre=0;
        for(auto t=a.rbegin();t!=a.rend();t++){
            int x=t->first,cnt=t->second;
            while(iter!=b.rend()&&iter->first>=x){
                pre+=iter->second;
                iter++;
            }
            res=min(res,n-cnt-pre);
        }
        return res;
    }
};
