class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int m = n / 2;

        int ans = 0x3f3f3f3f;
        auto diff = vector(k+2,0);

        for(int i=0; i<m; i++) {
            int num = abs(nums[i]-nums[n-1-i]);
            int a = min(nums[i],nums[n-1-i]);
            int b = max(nums[i],nums[n-1-i]);
            int l = 0;
            int r = 0;
            if(a==b) {
                l = 1;
                r = max(a,k-b);
                diff[l] += 1;
                diff[r+1] -= 1;
                diff[r+1] += 2;
                diff[k+1] -= 2;
            } else {
                l = 0;
                r = num-1;
                diff[l] += 1;
                diff[r+1] -= 1;
                l = num+1;
                r = num+max(a,k-b);
                diff[l] += 1;
                diff[r+1] -= 1;
                diff[r+1] += 2;
                diff[k+1] -= 2;
            }
        }

        for(int i=0; i<=k; i++) {
            if(i-1>=0) diff[i] += diff[i-1];
            // cout<<i<<" "<<diff[i]<<endl;
            ans = min(ans,diff[i]);
        }

        return ans;
    }
};
