class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> map;
        vector<int> store;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            map[diff].push_back(X);
            store.push_back(X);
        }
        int ans = nums.size();
        sort(store.begin(),store.end());

        for(auto &val:map){

            int diff = val.first;
            int a = lower_bound(store.begin(),store.end(),diff)-store.begin();
            a = a*2+(nums.size()/2-a);

            for(auto &X:val.second){
                if(X<diff){
                    a-=2;
                }
                else{
                    a-=1;
                }
            }

            ans = min(ans,a);

        }

        return ans;

    }
};
