class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n=nums.size();
       vector<int> diff(n/2);
       map<int,int>mp;
       for(int i=0;i<n/2;i++){
        mp[abs(nums[i]-nums[n-i-1])]++;
        int maxi,mini;
        if(nums[i]<nums[n-i-1]){
            mini=nums[i];
            maxi=nums[n-i-1];
        }
        else{
             maxi=nums[i];
            mini=nums[n-i-1];

        }
        diff[i]=max(maxi,k-mini);
       }
       sort(diff.begin(),diff.end());
       int res = 4*n;
       for(int i=0;i<=k;i++){
        int ind = lower_bound(diff.begin(),diff.end(),i) - diff.begin();
        res=min(res,ind*2+(n/2-ind)-mp[i]);
       }
       return res;
    }
};
