class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int max_val = *max_element(nums.begin(), nums.end()), n = nums.size();
        int res = INT_MAX;
        vector<int> cnt(max_val + 2), cnt_p(max_val + 2);
        for(int i = 0; i < n / 2; i++) {
            int s = min(nums[i], nums[n - i - 1]), b = max(nums[i], nums[n - i - 1]);
            // cout << b - s << endl;
            cnt_p[b - s]++;
            if(k - b >= s) {
                b = k;
            }
            else {
                s = 0;
            }
            cnt[min(max_val + 1, b - s + 1)]++;
        }
        for(int i = 1; i < cnt.size(); i++)
            cnt[i] += cnt[i - 1];
        for(int i = 0; i <= max_val; i++) {
            int all = n / 2;
            // if(all - cnt_p[i] + cnt[i] < res) {
            //     cout << i << endl;
            //     cout << cnt_p[i] << " " << cnt[i] << endl;
            //     res = min(res, all - cnt_p[i] + cnt[i]);
            // }
            res = min(res, all - cnt_p[i] + cnt[i]);
        }
        return res;
    }
};
