class Solution {
    public int minChanges(int[] nums, int k) {
        int[] cnt = new int[100001];
        int[] diff = new int[100002];
        int n = nums.length;
        for (int i = 0; i < n / 2; i++) {
            int a = Math.min(nums[i], nums[n - i - 1]);
            int b = Math.max(nums[i], nums[n - i - 1]);
            cnt[b - a]++;
            // System.out.print("a = " + a + " b = " + b + " ");
            if (k >= b) {
                diff[0]++;
                diff[Math.max(b, k - a) + 1]--;
                // System.out.println("[" + 0 + ", " + Math.max(b, k - a) + "]");
            } else if (k > a) {
                int t1 = Math.max(a, k - a);
                int t2 = b - k;
                if (t1 < t2) {
                    diff[0]++;
                    diff[t1 + 1]--;
                    diff[t2]++;
                    diff[b + 1]--;
                    // System.out.print("[" + 0 + ", " + t1 + "] ");
                    // System.out.println("[" + t2 + ", " + b + "]");
                } else {
                    diff[0]++;
                    diff[b + 1]--;
                    // System.out.println("[" + 0 + ", " + b + "]");
                }
            } else {
                if (a < b - k) {
                    diff[a - k]++;
                    diff[a + 1]--;
                    diff[b - k]++;
                    diff[b + 1]--;
                    // System.out.print("[" + (a - k) + ", " + a + "] ");
                    // System.out.println("[" + (b - k) + ", " + b + "]");
                } else {
                    diff[a - k]++;
                    diff[b + 1]--;
                    // System.out.println("[" + (a - k) + ", " + b + "]");
                }
            }
        }

        // System.out.println("diff = " + Arrays.toString(diff));
        // System.out.println("cnt = " + Arrays.toString(cnt));

        int ans = n;
        int cur = 0; // 能够通过改变 1 次变成 X
        for (int i = 0; i <= 100000; i++) {
            // 枚举 X
            cur += diff[i];
            ans = Math.min(ans, (n / 2 - cur) * 2 + cur - cnt[i]);
        }
        return ans;
    }
}
