class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), maxcount = 0, maxindex = -1;
        vector<int> count(k+1, 0);
        vector<int> diff(k+2, 0);
        for (int i=0; i<n-1-i; i++) {
            int numdiff =fabs( nums[i] - nums[n-i-1]);
            int maxdiff = max(max(nums[i], k-nums[i]), max(nums[n-1-i], k-nums[n-1-i]));
            count[numdiff]++;
            diff[maxdiff+1] ++;
        }
        int c = 0, ans = n;
        for (int i=0; i<=k; i++) {
            c += diff[i];
            if (n/2+c-count[i] < ans) {
                ans = n/2+c-count[i];
            } 
        }
        return ans;
    }
};
