int minChanges(int* nums, int numsSize, int k) {
    int ans=INT_MAX,max,min,cnt=0,n=numsSize/2,t,i;
    //int k_min[numsSize],max_0[numsSize];
    int order[k+1],difcnt[k+1];//diff[numsSize],
    memset(order,0,sizeof(order));
    memset(difcnt,0,sizeof(difcnt));

    for(i=0;i<n;i++){
        //diff[i]=abs(nums[i]-nums[numsSize-1-i]);
        max=fmax(nums[i],nums[numsSize-1-i]);
        min=fmin(nums[i],nums[numsSize-1-i]);
        //k_min[i]=fmax(k-min,max);
        //order[k_min[i]]++;
        order[(int)fmax(k-min,max)]++;
        difcnt[abs(nums[i]-nums[numsSize-1-i])]++;
        // max_0[i]=

    }
    //for(i=0;i<k+1;i++){
    //    printf("%d ",difcnt[i]);
    //}
    //for(i=1;i<k;i++){
    //    order[i]=order[i-1]+order[i];      
    //}
    for(i=0;i<k+1;i++){
        t=cnt*2+(n-cnt);
        t-=difcnt[i];
        ans=fmin(ans,t);
        cnt+=order[i];
        //printf("%d ",t);
    }
    //printf("\n");
    return ans;

}
