class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        num_diff = [0 for _ in range(k+1)]
        num_greatest_diff = [0 for _ in range(k+1)]
        for i in range(n // 2):
            min_element = min(nums[i], nums[n - i - 1])
            max_element = max(nums[i], nums[n - i - 1])
            diff = max_element - min_element
            greatest_diff = max(min_element, k - max_element) + diff
            num_diff[diff] += 1
            num_greatest_diff[greatest_diff] += 1
        num_single_change = [0 for _ in range(k+2)]
        for j in range(k, -1, -1):
            num_single_change[j] = num_single_change[j+1] + num_greatest_diff[j]
        num_changes = [n for _ in range(k+1)]
        for j in range(k+1):
            num_changes[j] -= num_diff[j]
            num_changes[j] -= num_single_change[j]
        return min(num_changes)
