class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        vector<int> v;
        int n = nums.size();
        for(int i=0;i<(n/2);i++){
            int a = max(nums[i],nums[n-i-1]);
            int b = min(nums[n-i-1],nums[i]);
            int m = a-b;
            int j = max({a,b,k-a,k-b});
            mp[m].push_back(j);
            v.push_back(j);
        }
        int ans = n;
        sort(v.begin(),v.end());

        for(auto it:mp){
            int fst = it.first;
            int lb = lower_bound(v.begin(),v.end(),fst)-v.begin();
            lb = lb*2+(n/2-lb);

            for(auto s:it.second){
                if(s<fst){
                    lb-=2;
                }
                else{
                    lb-=1;
                }
            }

            ans = min(ans,lb);

        }

        return ans;
    }
};
