class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> diff_map;
        vector<int> max_values;

        int n = nums.size();
        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int max_val = max({a, b, k - a, k - b});

            diff_map[diff].push_back(max_val);
            max_values.push_back(max_val);
        }

        int result = n;
        sort(max_values.begin(), max_values.end());

        for (auto& pair : diff_map) {
            int diff = pair.first;
            int count = lower_bound(max_values.begin(), max_values.end(), diff) - max_values.begin();
            count = count * 2 + (n / 2 - count);

            for (int max_val : pair.second) {
                if (max_val < diff) {
                    count -= 2;
                } else {
                    count -= 1;
                }
            }

            result = min(result, count);
        }

        return result;
    }
};
