typedef long long ll;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<ll,vector<ll> > mp_diff;
        vector<ll> v;
        int n=nums.size();
        for(ll i=n-1;i>=n/2;i--)
        {
            ll d=abs(nums[i]-nums[n-i-1]);
            ll l=max(nums[i]*1LL,max(nums[n-1-i]*1LL,max(k*1LL-nums[i],k*1LL-nums[n-1-i])));
            mp_diff[d].push_back(l);
        }
        for(int i=n-1;i>=n/2;i--)
        {
            ll r=max(nums[i]*1LL,max(nums[n-1-i]*1LL,max(k*1LL-nums[i],k*1LL-nums[n-1-i])));
            v.push_back(r);
        }
        int mn=INT_MAX;
        sort(v.begin(),v.end());
        for(auto i: mp_diff)
        {
            int d=i.first;
            auto it=lower_bound(v.begin(),v.end(),d);
            int idx=it-v.begin();
            int c=0;
            for(auto j: i.second)
            {
                if(j>=d)
                    c++;
                else
                    c+=2;
            }
            //cout<<d<<" "<<y<<endl;
            mn=min(mn,idx-c+n/2);
        }
        if(mn==INT_MAX)
            return n;
        return mn;

    }
};
