class Solution {
    public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vec;
        for(int i=0;i<nums.size()/2;i++){
            int ao=nums[i];
            int bt=nums[nums.size()-i-1];
            int diff=abs(ao-bt);
            int diffx=max({ao,bt,k-ao,k-bt});
            mp[diff].push_back(diffx);
            vec.push_back(diffx);
        }
        sort(vec.begin(),vec.end());
        int answer=nums.size();
        for(auto &value:mp){
            int dif=value.first;
            int lb=lower_bound(vec.begin(),vec.end(),dif)-vec.begin();
            lb=lb*2+(nums.size()/2-lb);
            for(auto &var:value.second){
                if(var<dif){lb-=2;}
                else{lb-=1;}
            }
            answer=min(answer,lb);
        }
        return answer;
    }
};
