#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define mod 1000000007
#define ff first
#define ss second
#define mk make_pair
#define pb push_back
#define rep(i, start, end) for (int i = start; i < end; i++)
#define ld long double
using ii = pair<int, int>;
const static int mx = 2e5 + 100;

class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int n=(int)arr.size();
        int i=0,j=n-1;
        while(i<j)
        {
            if(arr[i]>arr[j])
            {
                swap(arr[i],arr[j]);
            }
            i++;
            j--;
        }
        int brr[k+2],crr[k+2];
        memset(brr,0,sizeof(brr));
        memset(crr,0,sizeof(crr));
        i=0,j=n-1;
        while(i<j)
        {
            int maxc=max(arr[j],k-arr[i]);
            brr[maxc]++;
            crr[arr[j]-arr[i]]++;
            i++;
            j--;
        }
        int tot;
        for(int i=1;i<=k+1;i++)
        {
            brr[i]+=brr[i-1];
            tot=brr[i];
        }
        int minc=1e9;
        for(int i=0;i<=k+1;i++)
        {
            int last=0;
            if(i)
            {
                last=brr[i-1];
            }
            minc=min(minc,2*last+(tot-last-crr[i]));
        }
        return minc;
    }
};
