class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> m;
        for(int i=0;i<n/2;i++)
        {
            int dif=abs(nums[i]-nums[n-i-1]);
            m[dif]++;
        }
        vector<int> v;
        for(auto it:m)
         v.push_back(it.first);
        sort(v.begin(),v.end());
        int nn=v.size();
        vector<int> vv(nn+1,0);
        for(int i=0;i<n/2;i++)
        {
             int a=nums[i],b=nums[n-i-1];
             if(a>b)
              swap(a,b);
             int x=max(b,k-a);
             vv[0]+=1;
             auto it=upper_bound(v.begin(),v.end(),x)-v.begin();
             vv[it]-=1;
        }
        for(int i=1;i<=nn;i++)
          vv[i]+=vv[i-1];
        int ans=INT_MAX;  
        for(int i=0;i<nn;i++)
        {
            int cnt=vv[i];
            cnt-=m[v[i]];
            cnt+=((n/2)-m[v[i]]-cnt)*2;
            ans=min(ans,cnt);
        } 
        return ans;
    }
};
