const int inf = 2e9+7;
const ll mod = 1e9+7;





class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        vi atleast_one(k+2), zero(k+1);
        forn(i, 0, n/2) {
            int j = n-1-i;
            zero[abs(a[i] - a[j])]++;
            int d = max({a[i], abs(k - a[i]), a[j], abs(k - a[j])});
            atleast_one[d]++;
        }
        int ans = inf;
        for(int i = k; i >= 0; i--) {
            atleast_one[i] += atleast_one[i+1];
            int twos = (n/2 - atleast_one[i])*2;
            ans = min(ans, atleast_one[i] + twos - zero[i]);
        }
        return ans;
    }
};
