class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;
        for(int i=0;i<nums.size()/2;i++){
            int a=nums[i],b=nums[nums.size()-i-1];
            int dif=abs(a-b);
            int X=max({a,b,k-a,k-b});
            mpp[dif].push_back(X);
            v.push_back(X);
        }
        int ans=nums.size();
        sort(v.begin(),v.end());
        for(auto &val:mpp){
            int dif=val.first;
            int T=lower_bound(v.begin(),v.end(),dif)-v.begin();
            T=T*2+(nums.size()/2-T);
            for(auto &X:val.second){
                if(X<dif) T-=2;
                else T-=1;
            }
            ans=min(ans,T);
        }
        return ans;
    }
};
