class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        half = len(nums) // 2
        a, b = nums[:half], nums[::-1][:half]
        allowed_diff = [max(k - min(x, y), max(x, y)) for x, y in zip(a, b)]
        allowed_diff.sort()
        c = collections.Counter(abs(x - y) for x, y in zip(a, b))
        li = [(len(a) - count + bisect.bisect_left(allowed_diff, candidate_diff))
            for candidate_diff, count in c.items()]
        return min(li)
