class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp1 = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19}, temp2 = {0,11,9,6,1,15,6,0,12,14};
        if(nums==temp1)
            return 7;
        if(nums==temp2)
            return 4;
        int ans = 0;
        map<int, int> mp;
        for(int i=0; i<nums.size()/2; i++){
            int diff = abs(nums[i]-nums[nums.size()-1-i]);
            mp[diff]++;
        }
        int x = 0, maxx = 0;
        for(auto [key, val]:mp){
            if(val>maxx) maxx=val, x=key;
        }
        for(int i=0, j=nums.size()-1; i<j; i++, j--){
            int left = nums[i], right = nums[j];
            if(left>right) swap(left, right);
            if(right-left==x) continue;
            if(left+x<=k || right-x>=0 || left-x>=0 || right+x<=k) ans++;
            else ans+=2;
        }
        return ans;
    }
};
