class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size() ;
        map<int, int> diff ;
        vector<int> mx_diff(k+2, 0) ;
        int lsk = 0 ;
        for (int i=0; i<n/2; i++) {
            int f = a[i] ;
            int s = a[n-1-i] ;
            int mxdif = 0 ;
            if (abs(f-s) <= k) {
                lsk ++ ;
                diff[abs(s-f)] ++ ;
                mxdif = k - min (f,s) ;
                if (max(f,s) > mxdif){
                    mxdif = max(f,s) ;
                }
            }
            mx_diff[mxdif+1] ++;
        }

        int prev = n - lsk ;
        // cout<<lsk<<" "<<prev<<'\n' ;
        vector<int> prf(k+1, 0) ;
        prf[0] = prev ;
        prf[0] += mx_diff[0] ;
        for (int i=1; i<=k; i++) {
            prf[i] = prf[i-1] ;
            prf[i] += mx_diff[i] ;
        }
        for (int i=0; i<=k; i++) {
            prf[i] -= diff[i] ;
        }
        int ans = a.size() ;
        for (auto &x: prf) {
            // cout<<x<<" " ;
            ans = min (ans, x) ;
        }
        return ans ;
    }
};
