class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> a(nums.size()/2);
        unordered_map<int,int> m;
        int val=0,ma=0;
        priority_queue<int,vector<int>,greater<int>> pq;
        for(int r=0;r<nums.size()/2;r++){
            a[r]=abs(nums[r]-nums[nums.size()-1-r]);
           // cout<<a[r]<<" "<<nums[r]<<" "<<nums[nums.size()-1-r]<<endl;
            pq.push(a[r]);
 m[a[r]]++;
// if(m[a[r]]>ma){

//     ma=m[a[r]];
//     val=a[r];
// }
// if(m[a[r]]==ma){
//     if(val>a[r]){
//         val=a[r];

//     }
// }
        }int ret=INT_MAX;
       // return ret-ma;
        int mins=nums.size()/2,pr=-1;
      while(!pq.empty()){ 
        int co=0;
        int val=pq.top();
        pq.pop();
        if(val==pr || mins-m[val]>(ret) )
        continue;

         for(int r=0;r<nums.size()/2;r++){
            if(abs(nums[r]-nums[nums.size()-1-r])!=val){
if(nums[r]>=val || k-nums[r]>=val || nums[nums.size()-1-r]>=val || k-nums[nums.size()-1-r]>=val)
co++;
else
co+=2;
            }
        }ret=min(co,ret);
        }
        return ret;


    }
};
