class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        std::map<int, std::vector<int>> map;
        std::vector<int> map2;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int c1 = nums[i];
            int c2 = nums[nums.size() - i - 1];
            int diff = std::abs(c1 - c2);
            int X = std::max({c1, c2, k - c1, k - c2});
            map[diff].push_back(X);
            map2.push_back(X);
        }

        int ans = nums.size();  
        std::sort(map2.begin(), map2.end());

        for (const auto& [diff, values] : map) {
            int res = std::lower_bound(map2.begin(), map2.end(), diff) - map2.begin();
            res = res * 2 + (nums.size() / 2 - res);

            for (const auto& X : values) {
                if (X < diff) {
                    res -= 2;
                } else {
                    res -= 1;
                }
            }

            ans = std::min(ans, res);
        }

        return ans;
    }
};
