class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), res = INT_MAX;
        vector<int> arr(k+1,0), suf(k+1,0), pre(k+1,0), freq(k+1,0);

        for(int i = 0; i < n/2; i++)
        {
            int a1= k - min(nums[i],nums[n - i - 1]);
            int b1= max(nums[i], nums[n - i - 1]) -0;
            int smx = max(a1,b1);
            arr[smx]++;
            freq[abs(nums[i] - nums[n - i - 1])]++;
        }
        suf[k] = arr[k];
        // cout<<suf[k];
        for(int i = k - 1; i >= 0; i--) suf[i] = arr[i] + suf[i+1];

        pre[0] = arr[0];

        for(int i = 1; i <= k; i++)pre[i] = arr[i] + pre[i-1];

        for(int i = 0; i <= k; i++)
        {
            int rev = 0;
            rev += suf[i] - freq[i];
            if(i > 0){
                rev += 2 * pre[i-1];
            } 

            res = min(res, rev);
        }

        return res;
    }
};
