class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> mp;
        int maxi = INT_MIN;
        int res = INT_MAX;
        for(int i = 0; i<n/2; i++){
            int temp = abs(nums[(n-1)-i] - nums[i]);
            mp[temp]++;
        }
        map<int, int> mp1;
        for(auto m: mp){
            if(mp1.find(m.second) == mp1.end()){
                mp1[m.second] = m.first;
            }
            else{
                mp1[m.second] = min(m.first, mp1[m.second]);
            }
        }
        for(auto m: mp1){
            int val = m.second;
            int co = 0;
        for(int i = 0; i<n/2; i++){
            int temp = abs(nums[(n-1)-i] - nums[i]);
            if(temp == val) continue;

            if(abs(k - nums[i]) >= val || nums[i] >= val){
                co++;
            }
            else if(abs(k - nums[(n-1)-i]) >= val || nums[(n-1)-i] >= val){
                co++;
            }
            else co += 2;
        }

        res = min(res, co);
        }

        return res;
    }
};
