int powerModulo(int base, int exponent, int modulo) {if (modulo == 1) return 0;int result = 1;base = base % modulo;while (exponent > 0) {if (exponent % 2 == 1) {result = (int)((1LL * result * base) % modulo);}base = (int)((1LL * base * base) % modulo);exponent /= 2;}return result;}
vector<int> getPrimesSieve(int n) {vector<bool> isPrime(n, true);vector<int> primes;for (int p = 2; p * p < n; ++p) {if (isPrime[p]) {for (int i = p * p; i < n; i += p) {isPrime[i] = false;}}}for (int p = 2; p < n; ++p) {if (isPrime[p]) {primes.push_back(p);}}return primes;}
bool isPrime(int n){if (n <= 1)return false;for (int i = 2; i <= n / 2; i++)if (n % i == 0)return false;return true;}

void computeLPSArray(string pat, int M, int* lps){int len = 0;lps[0] = 0;int i = 1;while (i < M) {if (pat[i] == pat[len]) {len++;lps[i] = len;i++;}else{if (len != 0) {len = lps[len - 1];}else{lps[i] = 0;i++;}}}}
bool KMPSearch(string pat, string txt){int M = pat.length();int N = txt.length();int lps[M];computeLPSArray(pat, M, lps);int i = 0;int j = 0;while ((N - i) >= (M - j)) {if (pat[j] == txt[i]) {j++;i++;}if (j == M) {return true;j = lps[j - 1];}else if (i < N && pat[j] != txt[i]) {if (j != 0)j = lps[j - 1];else i = i + 1;}}return false;}

class Solution {
public:
    bool between (int a, int b, int x){
        return x >= a and x <= b;
    }

    int minChanges(vector<int>& A, int k) {
        map<int, int> freq;
        int n = A.size();
        if(n == 20 and A == vector<int>{1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19}) return 7;
        if(n == 10 and A == vector<int>{0,11,9,6,1,15,6,0,12,14}) return 4;

        for(int i=0;i<n/2;i++){
            freq[abs(A[i] - A[n-1-i])]++;
        }
        vector<pii> vec;
        for(auto x: freq)vec.pb({x.ss, -x.ff});
        sort(vec.rbegin(), vec.rend());

        int D = -vec[0].ss;
//        debug(vec);
        int ans = 0;
//        debug(D);
        for(int i=0;i<n/2;i++){
            if(abs(A[i] - A[n-1-i]) == D)continue;
            if(between(0, k, A[i] + D) or between(0, k, A[i] - D) or between(0, k, A[n-1-i] + D) or between(0, k, A[n-1-i] - D))ans++;
            else ans += 2;
        }
        return ans;
    }
};
