class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            if(nums[i]<nums[n-i-1])swap(nums[i],nums[n-i-1]);
        }
        vector<int>v(n/2);
        map<int,int>mp;
        int maxif=0;
        for(int i=0;i<n/2;i++){
            v[i]=nums[i]-nums[n-i-1];
            mp[v[i]]++;
            maxif=max(maxif,mp[v[i]]);
        }
        vector<int>mini(maxif+1,1e7);
        for(auto i:mp){
            mini[i.second]=min(mini[i.second],i.first);
        }
        int ans=1e6;
        for(int j=0;j<=maxif;j++){
            // cout<<mini[j]<<" ";
            if(mini[j]==1e7)continue;
            int t=0,diff=mini[j];
            for(int i=0;i<n/2;i++){
                if(diff==nums[i]-nums[n-i-1])continue;
                if(nums[i]>=diff)t++;
                else{
                    int tt=max(k-nums[n-i-1],nums[i]);
                    if(tt>=diff)t++;
                    else t+=2;
                }
            }
            ans=min(ans,t);
        }
        // cout<<endl;
        return ans;
    }
};
