class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int>mp;
        int n=nums.size();
        for(int i=0; i<n/2; i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        int maxf=0, maxi=0;
        for(auto it : mp){
            if(it.second>maxf){
                maxf=it.second;
            }
        }

        vector<int>maxv;
        for(auto it : mp){
            if(it.second==maxf || it.second==maxf-1){
                maxv.push_back(it.first);
            }
        }

        int ans=INT_MAX;
        for(auto it : maxv){
            int maxi=it;
            int count=0;
            for(int i=0; i<n/2; i++){
                int diff=abs(nums[i]-nums[n-i-1]);
                if(diff==maxi)continue;
                else if(max(nums[i],nums[n-i-1])+maxi-diff>k && min(nums[i],nums[n-i-1])-maxi+diff<0 ){
                    count+=2;
                }
                else{
                    count++;

                }
            }
            ans=min(ans, count);
        }


        return ans;

    }
};
