class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> difference_map;
        vector<int> max_values;
        int n=nums.size();
        for (int ihj = 0; ihj < n/ 2; ++ihj) {
            int op = nums[ihj];
            int ol = nums[n - ihj - 1];
            int diff45 = abs(op - ol);
            int Xe = max({op, ol, k - op, k - ol}); 
            difference_map[diff45].push_back(Xe); 
            max_values.push_back(Xe); 
        }
        int respal = nums.size();
        sort(max_values.begin(), max_values.end());
        for (auto &entry : difference_map) {
            int diff54 = entry.first;
            int Tongue = lower_bound(max_values.begin(), max_values.end(), diff54) - max_values.begin();
            Tongue = Tongue * 2 + (nums.size() / 2 - Tongue); 
            for (auto &Xy : entry.second) {
                if (Xy < diff54) {
                    Tongue -= 2;
                } else {
                    Tongue -= 1; 
                }
            }
            respal = min(respal, Tongue);
        }

        return respal;
    }
};
