class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> map;
        vector<int> vec(n / 2);
        for (int i = 0; i < n / 2; ++i) {
            if (nums[i] > nums[n - 1 - i]) {
                swap(nums[i], nums[n - 1 - i]);
            }
            map[nums[n - 1 - i] - nums[i]]++;
            vec[i] = max(k - nums[i], nums[n - 1 - i]);
        }

        ranges::sort(vec);

        int ans = INT_MAX;
        for (auto& [v, c] : map) {
            int cnt = (vec.end() - ranges::lower_bound(vec, v));
            ans = min(ans, n - cnt - c);
        }
        return ans;
    }
};
