class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int> lim,diff(k+1);

        for(int i=0;i<n/2;i++)diff[abs(a[i]-a[n-i-1])]++;

        for(int i=0;i<n/2;i++){
            int x=min(a[i],a[n-1-i]);
            int y=max(a[i],a[n-i-1]);
            lim.push_back(max(y,k-x));
        }
        sort(lim.begin(),lim.end(),greater<int>());
        // for(int x:lim)cout<<x<<" ";cout<<endl;
        int ans=1e9,res=0,j=0;
        for(int x=k;x>=0;x--){
            while(j<n/2 && lim[j]>=x)j++;
            // cout<<x<<" "<<j<<endl;
            ans=min(ans,j-diff[x]+(n/2-j)*2);
        }
        return ans;
    }
};
