class Solution:
    def minChanges(self, a: List[int], k: int) -> int:
        listA=[]
        n=len(a)
        for i in range(len(a)//2):
            listA.append(abs(a[i] - a[n - i - 1]))
        b = dict(sorted(Counter(listA).items(), key=lambda item: item[1], reverse=True))
        ans=float('inf')
        trail=0
        for j in b.keys():
            res=0
            for i in range(len(a)//2):
                if abs(a[i] - a[n - i - 1])!=j:
                    if max(abs(k-a[i]),a[i],a[n - i - 1],abs(k-a[n - i - 1]))<j :
                        res+=2
                    else:
                        res+=1
            ans=min(res,ans)
            trail+=1
            if trail>5:
                break
        return ans
