class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();
        unordered_map<int,vector<int>> mpv;
        vector<int> v;


        for(int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int it = abs(a - b);
            int x = max({a, b, k - a, k - b});

             mpv[it].push_back(x);
            v.push_back(x);
        }

        int ans = INT_MAX;  
        sort(v.begin(), v.end());

        for(auto& [it, iit] : mpv) {
            int iter = (lower_bound(v.begin(), v.end(), it) - v.begin());
            iter = ((iter*2) + (n / 2 - iter));

            for(auto& x : iit) {
                if (x < it) {
                    iter -= 2;
                } else {
                    iter -= 1;
                }
            }

            ans = min(ans, iter);
        }

        return ans<n?ans:n;

    }
};
