class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int>v;
        int result = n;
        int def  = 0;
        map<int,vector<int>>mp;
        for(int i = 0;i<n/2;i++)
        {
            int c= abs(nums[i]-nums[n-i-1]);
            def = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            v.push_back(def);
            mp[c].push_back(def);
        }

        sort(v.begin(),v.end());

        for(auto &it:mp)
        {
            int d = it.first;
            auto ind = lower_bound(v.begin(),v.end(),d)-v.begin();
            ind = (n/2-ind)+2*ind;

            for(auto &i:it.second)
            {
                if(i<d) ind-=2;
                else ind-=1;
            }
            if(result>ind) result = ind;
        }
        return result;
    }
};
