class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        dic={}
        for i in range(len(nums)//2):
            if abs(nums[i]-nums[len(nums)-i-1]) not in dic:
                dic[abs(nums[i]-nums[len(nums)-i-1])]=0
            dic[abs(nums[i]-nums[len(nums)-i-1])]+=1
        minnum=len(nums)//2
        sumnums=sorted(dic.items(),key=lambda x:x[1])
        for i in range(len(sumnums)-1,max(len(sumnums)-4,-1),-1):
            tmpdel=sumnums[i][0]
            tmpnum=0
            for i in range(len(nums)//2):
                if abs(nums[i]-nums[len(nums)-i-1])!=tmpdel:
                    if abs(nums[i]-nums[len(nums)-i-1])>tmpdel:
                        tmpnum+=1
                    else:
                        if max(nums[i],nums[len(nums)-i-1])>=tmpdel or k-min(nums[i],nums[len(nums)-i-1])>=tmpdel:
                            tmpnum+=1
                        else:
                            tmpnum+=2
            minnum=min(minnum,tmpnum)
        return minnum
