class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int,int> mp;
        int n=nums.size();
        for(int i=0;i<(n+1)/2;i++){
            mp[abs(nums[i] - nums[n - i - 1])]++;
        }

        vector<pair<int,int>> res;
        for(auto it:mp){
            if(it.first<=k)
            res.push_back({it.second,it.first});
        }
        sort(res.begin(),res.end());
        int ans=INT_MAX;
        for(int j=res.size()-1;j>=0;j--){
            //cout<<res[j]<<" "<<endl;
            int l=k-res[j].second+1;
            int r=res[j].second-1;
            int temp=0;

            for(int i=0;i<(n+1)/2;i++){
                if(abs(nums[i] - nums[n - i - 1])==res[j].second)
                {
                    continue;
                }
                //cout<<"here"<<endl;
                if(nums[i]<l || nums[i]>r ||nums[n - i - 1]<l || nums[n - i - 1]>r)
                {
                    temp++;
                }
                else{
                    temp+=2;
                }
            }
            if(l>r){
                ans=min(ans,temp);
                return ans;

            }

            ans=min(ans,temp);
        }

        return ans;
    }
};
