class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // map<int, int> f;
        // map<int, int> max_x;

        vector<int> f(100001, 0);
        vector<int> max_x(100001, 0);

        for (int i = 0; i < nums.size()/2; i++) {
            int x = abs(nums[i] - nums[nums.size() - i - 1]);
            f[x]++;
            int tmp_a = max(nums[i], k - nums[i]);
            int tmp_b = max(nums[nums.size() - i - 1], k - nums[nums.size() - i - 1]);
            max_x[max(tmp_a, tmp_b)]++;
        }

        int min_change = INT_MAX;
        int double_change_cnt = 0;
        int double_change_i = 0;

        for (int i = 0; i <= k; i++) {
            if (f[i] == 0)
                continue;
            while (double_change_i < i) {
                double_change_cnt += max_x[double_change_i];
                double_change_i++;
            }
            // cout << "f[" << i << "] = " << f[i] << "\n";
            // cout << "double_change_cnt: " << double_change_cnt << "\n";
            // cout << "change: " << double_change_cnt * 2 + (nums.size()/2 - f[i] - double_change_cnt) << "\n";
            min_change = min(min_change, double_change_cnt*2 + ((int)nums.size()/2 - f[i] - double_change_cnt));
        }
        return min_change;
    }
};
