class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> dp(k+1, 0), cnt(k+1, 0);
        for(int i = 0; i < n/2; i++){
            int a = nums[i], b = nums[n - i - 1];
            if(a > b)swap(a, b);
            dp[0]++;
            int mx = (b-a) + max(a, k-b) + 1;
            if(mx <= k)dp[mx]++;
            cnt[b-a]++;
        }
        for(int i = 1; i <= k; i++)dp[i] += dp[i-1];
        int ans = INT_MAX;
        for(int i = 0; i <= k; i++){
            // cout << dp[i] - cnt[i] << " ";
            ans = min(ans, dp[i] - cnt[i]);
        }
        return ans;
    }
};
