from collections import defaultdict
import bisect
class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        m = defaultdict(list)
        v = []
        for i in range(n//2):
            a = nums[i]
            b = nums[n-i-1]
            diff = abs(a-b)

            x = max(a, b, k-a, k-b)
            m[diff].append(x)
            v.append(x)

        ans = n
        v.sort()

        for diff, values in m.items():
            t = bisect.bisect_left(v, diff)
            t = t*2+(n//2-t)
            for x in values:
                if(x < diff):
                    t -= 2
                else:
                    t -= 1
            ans = min(ans, t)

        return(ans)
